    mpl_matrix* m = mpl_matrix_new();
    mpl_matrix_set_nrows(ntax, m);
    mpl_matrix_set_ncols(nchar, m);
    mpl_matrix_set_nnodes(2 * ntax, m);
    mpl_matrix_attach_rawdata(rawmatrix, m);
